import React, { useState, useRef, useEffect } from "react";
import "../Styles/TrainerSpace.css";
import { Container, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import backgroundImage from "../assets/background.jpeg";
import Image from "react-bootstrap/Image";
import logo from "../assets/logoSite.jpeg"
import { connect } from "react-redux";
import { BsGeoAlt, BsFillEnvelopeFill } from "react-icons/bs";
import { FiPhoneCall } from "react-icons/fi";
import axios from "axios";
import { baseURL } from "../constants";
import Listformations from "./Listformations"
import { login, setisauth, setisadmin, setlistvalue,setcurrenttrainer } from "../Actions/loginActions";
import { setvalueauth } from "../Actions/loginActions";

function TrainerSpace(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userDetails, setUserDetails] = useState({});
  const [name, setName] = useState("");
  const [formations, setFormations] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentFormation, setCurrentFormation] = useState({});
  const [formationname, setFormationName] = useState("");
  const [isChoosed, setIsChoosed] = useState(0);





  const didMountRef = useRef(false);
  useEffect(() => {
    console.log(props.listvalue);
    props.setlistvalue(0);

    if (props.admin) {

      getUsers();
      fetchUserDetails();

    } else {
      fetchUserDetails();

    }

    if (didMountRef.current) {

      // if login success, go to home screen
      if (props.isAuth) {
        //props.setisauth();
        //alert("Is Done")
        //props.setvalueauth(1);
        if (props.admin) {
          forceUpdate();
          getUsers();
          fetchUserDetails();

        }
        else {
          forceUpdate();
          fetchUserDetails();
        }

      } else if (!props.isAuth && !props.isLoading) {
        alert(props.errMsg);
      }
    } else {
      didMountRef.current = true;
    }
  }, [props.isAuth, props.isLoading]);
  const forceUpdate = React.useReducer(bool => !bool)[1];

  function getUsers() {
    axios({
      method: "post",
      url: "/getUsers",
      baseURL: baseURL,


    })
      .then((res) => {

        setUsers(res.data.results);



      })
      .catch((err) => console.log(err));
  }
  function fetchUserDetails() {
    axios({
      method: "post",
      url: "/getUserDetails",
      baseURL: baseURL,
      data: {
        userid: props.userId,
      },

    })
      .then((res) => {
        setUserDetails(res.data.results[0]);
        setName(res.data.results[0].name);
        setFormations(res.data.results[0].formations)


      })
      .catch((err) => console.log(err));
  }

  function loginHandler() {
    const loginData = {
      email: email,
      password: password,
    };
    // calling login() dispatch function
    props.login(loginData);
  };
  const listFormations = () => {
    if(isChoosed===0){
    return(
    formations.map((item) => (
      <div class="formDiv0"  >
        <a onClick={()=> {setIsChoosed(1);setCurrentFormation(item)}}  >
          <div class="formDiv" ><p class="formName" >{item.formationName.toUpperCase()}</p>
          </div>


        </a>
      </div>
    ))
    );
    }
    else {
      return (
        <div> 
          <p class="detail"> NOM : {currentFormation.formationName.toUpperCase()}</p>
          <p class="detail"> DESCRIPTION : {currentFormation.description}</p>
          <p class="detail"> LIEN : <a href={currentFormation.link}>{currentFormation.link}</a></p>

          </div>
      )
    }

  }
  /*const listFormations = formations.map((item) => (
    <div class="formDiv0"  >
      <a href={item.link}  >
        <div class="formDiv" ><p class="formName" >{item.formationName}</p>
        </div>


      </a>
    </div>
  ))*/

  const listUsers = users.map((item) => (

    <div class="formDiv0"  >

      <div class="formDiv1" >
        <Container className="Roww" >
          <Row >
            <Col sm={7} >
              <p class="formName" >{item.name}</p>
            </Col>
            <Col sm={5} >
              <a  onClick={() => {
                console.log(item);
                setcurrenttrainer(item._id);
                props.setlistvalue(item._id);

              }} > <p class="list"> Liste des formations</p> </a>
            </Col>
          </Row>
        </Container>

      </div>



    </div>
  ))

  const renderScreen = () => {
    if (props.isAuth) {
      if (props.admin) {

        if (props.listvalue !== 0) {
          return (
            <div>
              <Listformations/>
            </div>
          )

        }
        else {
          return (
            <div>


              <Container className="center0">
                <Container>
                  <Row>
                    <Col sm={6}>
                      <div ><p class="clientSpace">ESPACE FORMATEUR</p></div>
                    </Col>
                    <Col sm={6}>
                      <div class="Name">NOM ET PRNOM : ADMIN</div>
                    </Col>

                  </Row>


                </Container>
                <Container>
                    <Row>
                        <Col sm={8}>
                        <p class="formationList">LISTE D'UTILISATEURS </p>
                         </Col>
                        <Col sm={4} className="colll1" >
                            <div>
                                <div class="addDiv">
                                    <a  onClick={() => props.setisauth()} ><p class="add">Se dconnecter</p></a>
                                </div>
                            </div>
                        </Col>

                    </Row>


                </Container>



                <div>{listUsers}</div>
              </Container>


              <Container className="cont4">
                <Row className="cont41">
                  <Col sm={3}>
                    <div class="col4">
                      <img src={logo} class="logo"></img>

                    </div>
                  </Col>
                  <Col sm={3}>
                    <div class="col4">
                      <p class="contactUs">Nous contacter :</p>
                      <Row className="rowIcons">
                        <Col sm={4} xs={4}>
                          <BsGeoAlt class="icon" />
                        </Col>
                        <Col sm={4} xs={4}  >
                          <FiPhoneCall class="icon" />
                        </Col>
                        <Col sm={4} xs={4} >
                          <BsFillEnvelopeFill class="icon" />
                        </Col>
                      </Row>

                    </div>
                  </Col>
                  <Col sm={3}>
                    <div class="col4">
                      <p class="legal">Mentions lgales</p>
                      <p class="condition">Confidentialit</p>
                      <p class="condition">Conditions Gnrales</p>

                    </div>
                  </Col>
                  <Col sm={3}>
                    <div class="col4">
                      <p class="legal">Qui sommes nous ?</p>
                      <p class="condition">Blog</p>
                      <p class="condition">Former et recruter</p>

                    </div>
                  </Col>
                </Row>
              </Container>
              <a onClick={() => props.setisauth()}>HERRRRRRE</a>

            </div>

          )
        }
      }
      else {
        return (
          <div>
            <Container className="center0">
              <Container>
                <Row>
                  <Col sm={6}>
                    <div ><p class="clientSpace">ESPACE FORMATEUR</p></div>
                  </Col>
                  <Col sm={6}>
                    <div class="Name">NOM ET PRNOM : {name}</div>
                  </Col>

                </Row>


              </Container>


              <Container>
                    <Row>
                        <Col sm={8}>
                        <p class="formationList"><a  onClick={()=> setIsChoosed(0)}>LISTE DES FORMATIONS </a></p>
                         </Col>
                        <Col sm={4} className="colll1" >
                            <div>
                                <div class="addDiv">
                                    <a  onClick={() => props.setisauth()} ><p class="add">Se dconnecter</p></a>
                                </div>
                            </div>
                        </Col>

                    </Row>


                </Container>

              <div>{listFormations()}</div>
            </Container>





            <Container className="cont4">
              <Row className="cont41">
                <Col sm={3}>
                  <div class="col4">
                    <img src={logo} class="logo"></img>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="contactUs">Nous contacter :</p>
                    <Row className="rowIcons">
                      <Col sm={4} xs={4}>
                        <BsGeoAlt class="icon" />
                      </Col>
                      <Col sm={4} xs={4}  >
                        <FiPhoneCall class="icon" />
                      </Col>
                      <Col sm={4} xs={4} >
                        <BsFillEnvelopeFill class="icon" />
                      </Col>
                    </Row>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="legal">Mentions lgales</p>
                    <p class="condition">Confidentialit</p>
                    <p class="condition">Conditions Gnrales</p>

                  </div>
                </Col>
