// import {Alert} from 'react-native';
import AsyncStorage from "@react-native-community/async-storage";
import {
  LOGIN_REQUEST,
  LOGIN_SUCCESS,
  LOGIN_FAILURE,
  SET_AUTH_TOKEN,
  SET_CURRENT_USER,
  LOGOUT_USER,
  SET_VALUE_AUTH,
  SET_IS_AUTH
} from "../Actions/loginActions";

// init state for login
const initState = {
  isLoading: false,
  isAuth: false,
  loginData: {},
  errMsg: "",
  idUser: "",
  valueAuth: 0,
};

const loginReducer = (state = initState, action) => {
  switch (action.type) {
    case LOGIN_REQUEST:
      return {
        ...state,
        isLoading: true,
      };
    case LOGIN_SUCCESS:


      return {
        isAuth: true,
        isLoading: false,
        loginData: action.payload,
        errMsg: "",
        idUser: action.payload.id,
      };
    case LOGIN_FAILURE:
      AsyncStorage.removeItem("token");
      return {
        isLoading: false,
        isAuth: false,
        loginData: {},
        errMsg: action.payload,
      };
    case SET_CURRENT_USER:
      return {
        ...state,
        userDetails: action.payload,
        isAuth: true,
      };
    case SET_VALUE_AUTH:
      return {
        ...state,
         valueAuth: action.payload,
      };
      case SET_IS_AUTH:
      return {
        ...state,
         isAuth: false,
      };
    case LOGOUT_USER:
      return {
        ...state,
        isAuth: false,
      };
    default:
      return state;
  }
};

export default loginReducer;
