import React, { Component } from "react";
import "../Styles/Welcome.css";

import { Container, Row, Col, Table } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

import backgroundImage from "../assets/background.jpeg";
import Image from "react-bootstrap/Image";

export default class Welcome extends Component {
  render() {
    return (
      <div class="body">
        <div class="section1">
          <Container>
            <Row className="col1">
              <Col style={styles.col} sm={6}>
                <div class="  mt-5 ">
                  <p class="p-5" className="title">
                    Une offre de formation sur mesure
                  </p>

                  <p class="secondtitle">
                    Formation et conseil, EUREKA vous accompagne dans vos
                    projets
                  </p>
                </div>
              </Col>

              <Col style={styles.col} sm={6}>
                <div className="justify-content-center">
                  <img
                    src={backgroundImage}
                    class=" p-3 justify-content-center img-fluid  "
                  />
                </div>
              </Col>
            </Row>
          </Container>
        </div>

        <div class="section2">
          <Container>
            <Row>
              <Col style={styles.col} sm={6}>
                <p class="title1">EUREKA Formation</p>

                <p className="text">
                  Vous êtes acteur de la restauration et du commerce, vous
                  souhaitez recruter et former votre personnel ? Vous pouvez
                  maintenant nous déléguer le recrutement de vos collaborateurs
                  , l’élaboration du contenu de la formation et son animation
                  par des formateurs professionnels de votre métier. Nous avons
                  vous accompagnons dans le cadre d’une formation 100% financée
                  jusqu’à 400 heures. Vous voulez en savoir plus
                </p>
              </Col>

              <Col style={styles.col} sm={6}>
                <div className="justify-content-center">
                  <img
                    src={backgroundImage}
                    class=" p-3 justify-content-center img-fluid  "
                  />
                </div>
              </Col>
            </Row>
          </Container>
        </div>
      </div>
    );
  }
}
const styles = {
  grid: {
    paddingLeft: 0,
    paddingRight: 0,
  },
  row: {
    marginLeft: 0,
    marginRight: 0,
  },
  col: {
    paddingLeft: 0,
    paddingRight: 0,
  },
};
