import React, { Component } from "react";
import "../Styles/Welcome.css";

import { Container, Row, Col, Table } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

import backgroundImage from "../assets/background.jpeg";
import Image from "react-bootstrap/Image";

export default class Welcome extends Component {
  render() {
    return (
      <div class="body">
        <div>
          <img src={backgroundImage} class=" img-fluid " />
        </div>
        <div>
          <Container>
            <Row className="col1">
              <div>
                <Col style={styles.col} sm={6}>
                  <h1 class="p-5 text-white">Centre formation</h1>
                  <p class="p-4 fs-4 text-white">
                    {" "}
                    Vous êtes acteur de la restauration et du commerce, vous
                    souhaitez recruter et former votre personnel ? Vous pouvez
                    maintenant nous déléguer le recrutement de vos
                    collaborateurs , l’élaboration du contenu de la formation et
                    son animation par des formateurs professionnels de votre
                    métier. Nous avons vous accompagnons dans le cadre d’une
                    formation 100% financée jusqu’à 400 heures. Vous voulez en
                    savoir plus
                  </p>

                  <div class="mt-4"></div>
                </Col>
              </div>
              <Col sm={6}>
                <div>
                  <img src={backgroundImage} class=" img-fluid " />
                </div>
              </Col>
            </Row>
          </Container>
        </div>

        <div>
          {" "}
          <p class="mt-4 fs-4 text-black">
            {" "}
            Vous êtes acteur de la restauration et du commerce, vous souhaitez
            recruter et former votre personnel ? Vous pouvez maintenant nous
            déléguer le recrutement de vos collaborateurs , l’élaboration du
            contenu de la formation et son animation par des formateurs
            professionnels de votre métier. Nous avons vous accompagnons dans le
            cadre d’une formation 100% financée jusqu’à 400 heures. Vous voulez
            en savoir plus
          </p>
        </div>
      </div>
    );
  }
}
const styles = {
  grid: {
    paddingLeft: 0,
    paddingRight: 0,
  },
  row: {
    marginLeft: 0,
    marginRight: 0,
  },
  col: {
    paddingLeft: 0,
    paddingRight: 0,
  },
};
