import React, { useState, useRef, useEffect } from "react";
import "../Styles/TrainerSpace.css";
import { Container, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import backgroundImage from "../assets/background.jpeg";
import Image from "react-bootstrap/Image";
import logo from "../assets/logoSite.jpeg"
import { connect } from "react-redux";
import { BsGeoAlt, BsFillEnvelopeFill } from "react-icons/bs";
import { FiPhoneCall } from "react-icons/fi";
import axios from "axios";
import { baseURL } from "../constants";
import Listformations from "./Listformations"
import { login, setisauth, setisadmin } from "../Actions/loginActions";
import { setvalueauth } from "../Actions/loginActions";

function TrainerSpace(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userDetails, setUserDetails] = useState({});
  const [name, setName] = useState("");
  const [formations, setFormations] = useState([]);
  const [users, setUsers] = useState([]);
  const [listvalue, setListvalue] = useState(0);




  const didMountRef = useRef(false);
  useEffect(() => {

    if (props.admin) {

      getUsers();
      fetchUserDetails();

    } else {
      fetchUserDetails();

    }

    if (didMountRef.current) {

      // if login success, go to home screen
      if (props.isAuth) {
        //props.setisauth();
        //alert("Is Done")
        //props.setvalueauth(1);
        if (props.admin) {
          forceUpdate();
          getUsers();
          fetchUserDetails();

        }
        else {
          forceUpdate();
          fetchUserDetails();
        }

      } else if (!props.isAuth && !props.isLoading) {
        alert(props.errMsg);
      }
    } else {
      didMountRef.current = true;
    }
  }, [props.isAuth, props.isLoading]);
  const forceUpdate = React.useReducer(bool => !bool)[1];

  function getUsers() {
    axios({
      method: "post",
      url: "/getUsers",
      baseURL: baseURL,


    })
      .then((res) => {

        setUsers(res.data.results);



      })
      .catch((err) => console.log(err));
  }
  function fetchUserDetails() {
    axios({
      method: "post",
      url: "/getUserDetails",
      baseURL: baseURL,
      data: {
        userid: props.userId,
      },

    })
      .then((res) => {
        setUserDetails(res.data.results[0]);
        setName(res.data.results[0].name);
        setFormations(res.data.results[0].formations)


      })
      .catch((err) => console.log(err));
  }

  function loginHandler() {
    const loginData = {
      email: email,
      password: password,
    };
    // calling login() dispatch function
    props.login(loginData);
  };
  const listFormations = formations.map((item) => (
    <div class="formDiv0"  >
      <a href={item.link}  >
        <div class="formDiv" ><p class="formName" >{item.formationName}</p>
        </div>


      </a>
    </div>
  ))

  const listUsers = users.map((item) => (

    <div class="formDiv0"  >

      <div class="formDiv1" >
        <Container className="Roww" >
          <Row >
            <Col sm={7} >
              <p class="formName" >{item.name}</p>
            </Col>
            <Col sm={5} >
              <a href="###" onClick={() => setListvalue(1)} > <p class="list">Liste des formations</p> </a>
            </Col>
          </Row>
        </Container>

      </div>



    </div>
  ))

  const renderScreen = () => {
    if (props.isAuth) {
      if (props.admin) {
        console.log("dkhal");
        if (listvalue===1) {
          return(
            <div>
              <Listformations />
            </div>
          )

        }
        else {
          return (
            <div>


              <Container className="center0">
                <Container>
                  <Row>
                    <Col sm={6}>
                      <div ><p class="clientSpace">ESPACE FORMATEUR</p></div>
                    </Col>
                    <Col sm={6}>
                      <div class="Name">NOM ET PRNOM : ADMIN</div>
                    </Col>

                  </Row>


                </Container>


                <p class="formationList">LISTE D'UTILISATEURS </p>

                <div>{listUsers}</div>
              </Container>


              <Container className="cont4">
                <Row className="cont41">
                  <Col sm={3}>
                    <div class="col4">
                      <img src={logo} class="logo"></img>

                    </div>
                  </Col>
                  <Col sm={3}>
                    <div class="col4">
                      <p class="contactUs">Nous contacter :</p>
                      <Row className="rowIcons">
                        <Col sm={4} xs={4}>
                          <BsGeoAlt class="icon" />
                        </Col>
                        <Col sm={4} xs={4}  >
                          <FiPhoneCall class="icon" />
                        </Col>
                        <Col sm={4} xs={4} >
                          <BsFillEnvelopeFill class="icon" />
                        </Col>
                      </Row>

                    </div>
                  </Col>
                  <Col sm={3}>
                    <div class="col4">
                      <p class="legal">Mentions lgales</p>
                      <p class="condition">Confidentialit</p>
                      <p class="condition">Conditions Gnrales</p>

                    </div>
                  </Col>
                  <Col sm={3}>
                    <div class="col4">
                      <p class="legal">Qui sommes nous ?</p>
                      <p class="condition">Blog</p>
                      <p class="condition">Former et recruter</p>

                    </div>
                  </Col>
                </Row>
              </Container>
              <a onClick={() => props.setisauth()}>HERRRRRRE</a>

            </div>

          )
        }
      }
      else {
        return (
          <div>
            <Container className="center0">
              <Container>
                <Row>
                  <Col sm={6}>
                    <div ><p class="clientSpace">ESPACE FORMATEUR</p></div>
                  </Col>
                  <Col sm={6}>
                    <div class="Name">NOM ET PRNOM : {name}</div>
                  </Col>

                </Row>


              </Container>


              <p class="formationList">LISTE DE FORMATIONS </p>

              <div>{listFormations}</div>
            </Container>





            <Container className="cont4">
              <Row className="cont41">
                <Col sm={3}>
                  <div class="col4">
                    <img src={logo} class="logo"></img>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="contactUs">Nous contacter :</p>
                    <Row className="rowIcons">
                      <Col sm={4} xs={4}>
                        <BsGeoAlt class="icon" />
                      </Col>
                      <Col sm={4} xs={4}  >
                        <FiPhoneCall class="icon" />
                      </Col>
                      <Col sm={4} xs={4} >
                        <BsFillEnvelopeFill class="icon" />
                      </Col>
                    </Row>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="legal">Mentions lgales</p>
                    <p class="condition">Confidentialit</p>
                    <p class="condition">Conditions Gnrales</p>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="legal">Qui sommes nous ?</p>
                    <p class="condition">Blog</p>
                    <p class="condition">Former et recruter</p>

                  </div>
                </Col>
              </Row>
            </Container>
            <a onClick={() => { props.setisauth(); props.setisadmin() }}>HERRRRRRE</a>



          </div>

        );
      }
    }
    else {
      return (
        <div >

          <Container className="center0">
            <Container>
              <p class="clientSpace">ESPACE FORMATEUR</p>
            </Container>

            <Container>
              <Row>
                <Col sm={6}>
                  <div class="loginDiv">
                    <p class="already">DJ FROMATEUR ?</p>
                    <p class="above">ADRESSE EMAIL *</p>
                    <input class="inputAccount"
                      placeholder="Renseignez votre email"
                      onChange={e => setEmail(e.target.value)} />
                    <p class="above">MOT DE PASSE *</p>

                    <input class="inputAccount"
                      placeholder="Saisissez votre mot de passe"
                      secureTextEntry={true}
                      onChange={e => setPassword(e.target.value)}
                      type="password" />

                    <p class="passForg" >Mot de passe oubli ?</p>
                    <a href="###" onClick={() => loginHandler()}>
                      <div class="logIn" >M'IDENTIFIER</div></a>
                  </div>
                </Col>
                <Col sm={6}>
                  <div class="loginDiv" >
                    <p class="already">NOUVEAU FORMATEUR</p>
                    <a href="/inscription" >
                      <div class="logIn" >CRER UN COMPTE</div></a>
                  </div>
                </Col>
              </Row>

            </Container>

          </Container>
          <div class="divEnd">
            <Container className="cont4">
              <Row className="cont41">
                <Col sm={3}>
                  <div class="col4">
                    <img src={logo} class="logo"></img>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="contactUs">Nous contacter :</p>
                    <Row className="rowIcons">
                      <Col sm={4} xs={4}>
                        <BsGeoAlt class="icon" />
                      </Col>
                      <Col sm={4} xs={4}  >
                        <FiPhoneCall class="icon" />
                      </Col>
                      <Col sm={4} xs={4} >
                        <BsFillEnvelopeFill class="icon" />
                      </Col>
                    </Row>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="legal">Mentions lgales</p>
                    <p class="condition">Confidentialit</p>
                    <p class="condition">Conditions Gnrales</p>

                  </div>
                </Col>
                <Col sm={3}>
                  <div class="col4">
                    <p class="legal">Qui sommes nous ?</p>
                    <p class="condition">Blog</p>
                    <p class="condition">Former et recruter</p>

                  </div>
                </Col>
              </Row>
            </Container>
          </div>
        </div>







      )
    }
  }

  return (

    <div>{renderScreen()}</div>

  );

}
const mapStateToProps = (state) => {
  return {
    // only map needed states here
    isLoading: state.loginReducer.isLoading,
    isAuth: state.loginReducer.isAuth,
    errMsg: state.loginReducer.errMsg,
    valueAuth: state.loginReducer.errMsg,
    userId: state.loginReducer.userId,
    admin: state.loginReducer.admin,
    listvalue:state.loginReducer.listvalue,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    // only map needed dispatches here
    login: (loginData) => dispatch(login(loginData)),
    setvalueauth: (value) => dispatch(setvalueauth(value)),
    setisauth: () => dispatch(setisauth()),
    setisadmin: () => dispatch(setisadmin()),
    setlistvalue: (value) => dispatch(setlistvalue(value)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(TrainerSpace);
